          </li>
        ))}
      </ul>
      <form onSubmit={(e) => {
        e.preventDefault();
        const title = e.target.elements.title.value;
        addTask(title);
        e.target.elements.title.value = ''; // Clear input after successful add
      }}>
        <input type="text" name="title" placeholder="Adicionar tarefa" />
        <button type="submit">Adicionar</button>
      </form>
    </div>
  );
};

export default List;
